aistorelab.online.nginx_config:
# Nginx Configuration for aistorelab.online
server {
    listen 80;
    listen 443 ssl http2;
    server_name aistorelab.online www.aistorelab.online;

    # SSL Configuration
    ssl_certificate /etc/letsencrypt/live/aistorelab.online/fullchain.pem;
    ssl_certificate_key /etc/letsencrypt/live/aistorelab.online/privkey.pem;

    # Security Headers
    add_header X-Frame-Options DENY;
    add_header X-Content-Type-Options nosniff;
    add_header X-XSS-Protection "1; mode=block";
    add_header Strict-Transport-Security "max-age=31536000; includeSubDomains";

    # Main site
    location / {
        proxy_pass http://localhost:8500;
        proxy_set_header Host $host;
        proxy_set_header X-Real-IP $remote_addr;
        proxy_set_header X-Forwarded-For $proxy_add_x_forwarded_for;
        proxy_set_header X-Forwarded-Proto $scheme;
    }

    # API Gateway
    location /api/ {
        proxy_pass http://localhost:8501/;
        proxy_set_header Host $host;
        proxy_set_header X-Real-IP $remote_addr;
    }
}

